const toggler=document.getElementById("ham"),menu=document.querySelector(".nav-menu"),header=document.querySelector("header");toggler.addEventListener("click",(function(){menu.classList.toggle("open")}));const accordions=document.querySelectorAll(".accordion");accordions.forEach((e=>{e.addEventListener("click",(function(e){if(e.target.matches(".accordion__question")){let t=this.querySelector(".active");t==e.target?e.target.classList.toggle("active"):(t&&t.classList.remove("active"),e.target.classList.add("active"))}}))})),window.addEventListener("scroll",(function(){let e=window.scrollY>0;header.classList.toggle("scrolling-active",e)}));